mapper.select:

	select interestName
	from interest
	where interestNo = #{interestNo}

mapper.insert:

	delete from mychallenge
	where challengeNo = #{challengeNo}
	and userNo = #{userNo}

mapper.select:

	select count(challengeNo)
	from bookmark
	where userNo = #{userNo}

mapper.delete:

	delete from bookmark
	where userNo = #{userNo}
	and challengeNo = #{challengeNo}

mapper.select:
	
		select  ( select count(userNo)
		          from mychallenge
		          where challengeNo = #{challengeNo}
		          and userNo = #{userNo}) userCount,
        		founder,
        		(select payment
		         from mychallenge
		         where challengeNo = #{challengeNo}
		         and userNo = #{userNo}) payment
		from mychallenge          
		where challengeNo = #{challengeNo}
		and userNo = #{userNo}

