mapper.select:
select rn, ort.userNo, ort.nickName, ort.boardNo, ort.challengeNo, ort.category, ort.title, ort.boardContent, ort.hit, ort.boardDate, boardDate2 from(select rownum rn, ot.userNo, ot.nickName, ot.boardNo, ot.challengeNo, ot.category, ot.title, ot.boardContent, ot.hit, ot.boardDate boardDate, boardDate2 from (select u.userNo userNo, u.nickName, b.boardNo boardNo, c.challengeNo challengeNo, b.category category, b.title title, b.content boardContent, b.hit hit, b.boardDate boardDate, to_char(boardDate,'YYYY-MM-DD') boardDate2 from users u, challenge c, board b where u.userNo = b.userNo and c.challengeNo = b.challengeNo and c.challengeNo = #{challengeNo}
and c.clgTitle like '%'||#{keyword}||'%'
and b.category = #{category}
order by boardNo desc) ot )ort where ort.rn >= #{startRnum} and ort.rn <= #{endRnum}

select count(*) from users u, challenge c, board b where u.userNo = b.userNo and c.challengeNo = b.challengeNo and c.challengeNo = #{challengeNo}
and c.clgTitle like '%'||#{keyword}||'%'
and b.category = #{category}
