mapper.update:

	update myChallenge
	set state = 2
	where challengeNo = #{challengeNo}
	and userNo = #{userNo}

mapper.delete:

	delete from review 
	where reviewNo = #{reviewNo}
	and userNo=#{userNo}

mapper.select:
	
	select  nvl(sum(amount), 0) sum
	from point
	where userNo=#{userNo}

	
		select  ( select count(userNo)
		          from mychallenge
		          where challengeNo = #{challengeNo}
		          and userNo = #{userNo}) userCount,
        		founder,
        		(select payment
		         from mychallenge
		         where challengeNo = #{challengeNo}
		         and userNo = #{userNo}) payment
		from mychallenge          
		where challengeNo = #{challengeNo}
		and userNo = #{userNo}
